'use strict';

module.exports = {
    stats: {
        longestCrawl: 0,
        currentCrawlCounter: 0,
        totalCounter: 0
    },

    getCurrentCrawlCounter: function () {
        return this.stats.currentCrawlCounter;
    },
    resetCurrentCrawlCounter: function () {
        if (this.stats.currentCrawlCounter > this.stats.longestCrawl) {
            this.stats.longestCrawl = this.stats.currentCrawlCounter;
        }
        this.stats.currentCrawlCounter = 0;
    },
    updateCurrentCrawlCounter: function () {
        this.stats.currentCrawlCounter += 1;
    },
    updateTotalCounter: function () {
        this.stats.totalCounter += 1;
    },
    getLongestCrawl: function () {
        return this.stats.longestCrawl;
    }
}
