const fs = require('fs');
const Log = require('log');

const config = require('config');

const normal = new Log('debug', fs.createWriteStream( `${config.logsPath}/appOut.log`, {flags: 'a'}) );
const error = new Log('error', fs.createWriteStream( `${config.logsPath}/appError.log`, {flags: 'a'}) );

module.exports = {
    debug: (...data) => {
        normal.debug(data.join())
    },
    error: (...data) => {
        error.error(data.join())
    },
    info: (...data) => {
        normal.info(data.join())
    }
};
