'use strict';

module.exports = {
    getTimeStamp: function() {
        const date  = new Date();
        const times = [date.getHours(), date.getMinutes(), date.getSeconds()];

        return times.reduce((accu, t) => accu + this.leftPad(t) + ':', '')
    },

    getMakroTimeStamp: function() {
        const date  = new Date();
        const times = [date.getMonth(), date.getDate()];

        return times.join('-');
    },

    leftPad: v => (v.toString().length === 1)
                ? 0 + v.toString()
                : v.toString(),

    getUnixTimeStamp: function () {
        return new Date().getTime() / 1000;
    }
};
